import React from 'react'
import { StyleSheet, View, Text, Dimensions, TouchableOpacity } from 'react-native'
import { MaterialCommunityIcons } from '@expo/vector-icons'
import { List } from 'react-native-paper';
import ButtonComp from '@src/components/Buttons/Buttons'
import { FlatGrid } from 'react-native-super-grid'
import { connect } from 'react-redux'
import Payment from '../Payment/Payment'

class Parking extends React.Component {
  constructor() {
    super()
    this.state = {
      active: false,
      payActive: true,
      items: [],
      activeOption : '',
      paymentOption: true,
      timer: "00:00:01"
    }
  }

  onPayment = () => {
    return (
      <Payment></Payment>
    )
  }


  onBack = () => {
    this.setState({ payActive: true })
  }

  checkTimer = () => {
    console.log(this.state.timer)
    if(this.state.timer == "00:00:00") {
      return (
        <MaterialCommunityIcons name="timer-sand-empty" 
           style={{ justifyContent:'center', alignItems: 'center', alignContent: 'center'}} 
           size={50} color="white" />
      )
    } else {
      return (
        <MaterialCommunityIcons name="timer-sand" 
        style={{ justifyContent:'center', alignItems: 'center', alignContent: 'center'}} 
        size={50} color="white" />
      )
    }
  }
  

  viewSpot = (spot) => {

    return (
      this.state.paymentOption ?
      <View style={styles.spot}>
        <View>
          <List.Item
            title={`Parking Space ${spot.parkSpace}`}
            titleStyle={{color: 'white', fontSize: 18}}
            description={`Current status of the spot is ${spot.status}`}
            descriptionStyle={{color: 'white', fontSize: 18}}
            left={ () => <MaterialCommunityIcons name="parking" 
            style={{ justifyContent:'center', alignItems: 'center', alignContent: 'center'}} 
            size={50} color="white" />}
          />
          <List.Item
           title={`Remaining Time`}
           titleStyle={{color: 'white', fontSize: 18}}
           description={`${this.state.timer}`}
           descriptionStyle={{color: 'white', fontSize: 18}}
           left={ () => this.checkTimer()}
          />
        </View>
        <View style={styles.doubleButton}>
          <ButtonComp 
           style={{ backgroundColor: this.props.balance == 0 ? 'gray' : '#00aeef', margin: 10, borderRadius: 8, width: 150, padding: 10, alignItems: 'center', justifyContent: 'center'}}
           disabled = {this.props.balance == 0} onPress={() => { console.log("Hello")}}>Pay</ButtonComp>
          <ButtonComp onPress={() => { this.onPayment, this.setState({ paymentOption: false }) }}>Add Funds</ButtonComp>
        </View>
        <View style={styles.rowButton}>
          <ButtonComp onPress={this.onBack}>Back</ButtonComp>
        </View>
      </View> :
      this.onPayment()
    )
  }

  renderPage = () => {
    const spot = this.state.items
    if(this.state.payActive == false) {
      return (
        this.viewSpot(spot)
      )
    }
  }

  Grid = () => {
    return (
      this.state.payActive ?
      this.Legend(): 
      this.renderPage()
    ) 
 
  }

  Legend = () => {
    const parkingSpaces = [
      {
          "parkSpace": 1,
          "status": "Active",
          "type": "Normal",
          "code": "#69c100"
      },
      {
          "parkSpace": 2,
          "status": "Active",
          "type": "Normal",
          "code": "#69c100"
      },
      {
          "parkSpace": 3,
          "status": "Active",
          "type": "Normal",
          "code": "#69c100"
      },
      {
          "parkSpace": 4,
          "status": "Active",
          "type": "Normal",
          "code": "#69c100"
      },
      {
          "parkSpace": 5,
          "status": "Taken",
          "type": "Normal",
          "code": "#bc0000"
      },
      {
          "parkSpace": 6,
          "status": "Reserved",
          "type": "Normal",
          "code": "#ffcb00"
      },
      {
          "parkSpace": 7,
          "status": "Taken",
          "type": "Normal",
          "code": "#bc0000"
      },
      {
          "parkSpace": 8,
          "status": "Active",
          "type": "Disabled",
          "code": "#69c100"
      },
      {
          "parkSpace": 9,
          "status": "Active",
          "type": "Disabled",
          "code": "#69c100"
      },
      {
          "parkSpace": 10,
          "status": "Taken",
          "type": "Disabled",
          "code": "#bc0000"
      }
  ]
    return (
      <>
        <Text style={styles.textStyle}>Legend</Text>
        <View style={styles.container}>
          <View style={styles.rows}>
            <Text style={styles.legendText}>Active</Text>
            <View style={{ backgroundColor: '#69c100', marginLeft: 5, width: 18, height: 18, borderRadius: 22 }} />
          </View>
          <View style={styles.rows}>
            <Text style={styles.legendText}>Taken</Text>
            <View style={{ backgroundColor: '#bc0000', marginLeft: 5, width: 18, height: 18, borderRadius: 22 }} />
          </View>
          <View style={styles.rows}>
            <Text style={styles.legendText}>Reserved</Text>
            <View style={{ backgroundColor: '#ffcb00', marginLeft: 5, width: 18, height: 18, borderRadius: 22 }} />
          </View>
        </View>
        <FlatGrid
        itemDimension={90}
        data={parkingSpaces}
        style={styles.gridView}
        spacing={10}
        renderItem={({ item }) => (
          <TouchableOpacity onPress={() => {this.setState({ items: item }),this.setState({ payActive: false }) }}>
            <View style={[styles.itemContainer, { backgroundColor: item.code }]}>
              <Text style={styles.itemName}>{item.parkSpace}</Text>
              <Text style={styles.itemCode}>{item.type}</Text>
            </View>
          </TouchableOpacity>
        )}
        /></>
    )
  }


  render() {
    return (
      <View style={styles.mainContainer}>
        {this.Grid()}
      </View>
    )
  }

}

const styles = StyleSheet.create({
  gridView: {
    marginTop: 10,
    height: Dimensions.get('screen').height,
    width: Dimensions.get('screen').width,
  },
  itemContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 3,
    borderTopWidth: 3,
    borderLeftWidth: 3,
    borderRightWidth: 3,
    borderTopColor: 'white',
    borderLeftColor: 'white',
    borderRightColor: 'white',
    width: 100,
    height: 130,
    margin: 10
  },
  itemName: {
    fontSize: 16,
    color: '#fff',
    fontWeight: '600',
  },
  itemCode: {
    fontWeight: '600',
    fontSize: 12,
    color: '#fff',
  },
  mainContainer: {
    flex: 2.2,
    borderColor: 'white',
    borderWidth: 0.2,
    width: Dimensions.get('screen').width,
    height: Dimensions.get('screen').height,
  },
  textStyle: {
    color: 'white',
    fontSize: 20,
    textAlign: 'center',
  },
  rows: {
    flexDirection: 'row',
    margin: 10
  },
  legendText: {
    color: 'white',
    fontSize: 14
  },
  container: {
    flexDirection: 'row',
    justifyContent: 'center'
  },
  spot: {
    flex:1,
    width: Dimensions.get('screen').width,
    height: Dimensions.get('screen').height,
    justifyContent: 'center'
  },
  doubleButton: {
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'row',
    borderRadius: 8,
    marginTop: 100
  },
  rowButton: {
    justifyContent: 'center',
    alignItems: 'center',
