import { userRegister_Failed, userRegister_Success } from '../../constants/constants'

const initialState = {
    loading:false,
    error: null,
    status: false,
    username: ''
}

const UserRequest = (state = initialState , action) => {
    switch(action.type) {
        case userRegister_Success:
            return { ...state, loading: true, error: null, username: action.payload.username}
        case userRegister_Failed:
            return {...state, loading: false, error: true, username: ''}
        default:
            return state
    }
}
export default UserRequest
